# Writes out the log to the file server for collation
Function Write-Log {
    param(
        [Parameter(Mandatory=$true)][String]$msg
    )
    $logtime = (get-date -Format "HH:mm:ss-ddMMyy")
    Add-Content -Path $loggingpath -Value $logtime" | "$env:Computername" | "$scriptVer" | "$msg
}

# Closes xStore
function close-Xstore {
        param(
            [Parameter(Mandatory=$true)][String]$closeXstore
        )

            #Where are the files that need to be removed stored?
            $xstorelockdir = @('C:\xstore\tmp','C:\xstore\tmp','C:\environment\tmp','C:\xstore-mobile\tmp','C:\eftlink\tmp');
            $xstoreProcessList = @((Get-Process | Where-Object {$_.ProcessName -ilike "xstore*"}),(Get-Process | Where-Object {$_.ProcessName -ilike "xmob*"}),(Get-Process | Where-Object {$_.ProcessName -ilike "xenv*"}))
            $runningProcCount = $xstoreProcessList.ProcessName.count

                #Add record to the list view field for action log.
                $time = (get-date -Format "HH:mm:ss")
                $listView.items.Insert(0,"[$time] Currently $runningProcCount xStore Processes Running.")

                if($closeXstore -ilike "y*"){

                            Write-log " : Closing xStore Software Stack, ($closeXstore) received."

                            if(($runningProcCount) -gt "0"){
                                foreach($ld in $xstorelockdir){
                                    if(test-path $ld){
                                        $lockfiles = Get-ChildItem -Path $ld | Where-Object {$_.Extension -ieq ".anchor"}
                                            foreach($l in $lockfiles){
                                                $anchorName = $l.Name
                                                if(test-path -Path $l.FullName){
                                                    #Add record to the list view field for action log.
                                                    $time = (get-date -Format "HH:mm:ss")
                                                    $listView.items.Insert(0,"[$time] Attempting Removal of $anchorName.")
                                                        Write-log " : Attempting Removal of $l."
                                                        Remove-Item -Path $l.FullName -force
                                                }else{
                                                    #Add record to the list view field for action log.
                                                    $time = (get-date -Format "HH:mm:ss")
                                                    $listView.items.Insert(0,"[$time] $anchorName has not been found.")
                                                        Write-log " : $l has not been found."
                                                }
                                            }
                                    }else{
                                        #Add record to the list view field for action log.
                                        $time = (get-date -Format "HH:mm:ss")
                                        $listView.items.Insert(0,"[$time] $ld does not exist.")
                                            Write-log " : $ld does not exist."
                                    }
                                }

                                $closeingXstore = 0
                                while((@((Get-Process | Where-Object {$_.ProcessName -ilike "xstore*"}),(Get-Process | Where-Object {$_.ProcessName -ilike "xmob*"}),(Get-Process | Where-Object {$_.ProcessName -ilike "xenv*"})).ProcessName).count -ge '1' -and $closeingXstore -le '20'){
                                        start-sleep -Seconds 1
                                        $closeingXstore += 1
                                }
                                    if($closeingXstore -le '20'){
                                        $timeToclose = ($closeingXstore)
                                            #Add record to the list view field for action log.
                                            $time = (get-date -Format "HH:mm:ss")
                                            $listView.items.Insert(0,"[$time] xStore closed in $timeToclose seconds.")
                                                Write-Log " : Xstore appears to be closed and it took $timeToclose seconds."
                                    }else{
                                        Write-Log " : ERROR - failed to close xStore."
                                            #Add record to the list view field for action log.
                                            $time = (get-date -Format "HH:mm:ss")
                                            $listView.items.Insert(0,"[$time] WARN - xStore has not closed in $timeToclose seconds.")
                                    }

                            }else{
                                #Add record to the list view field for action log.
                                $time = (get-date -Format "HH:mm:ss")
                                $listView.items.Insert(0,"[$time] xStore is not running.")
                                Write-log " : xStore is not running on $ENV:ComputerName @ $time."
                            }
                }else{
                    Write-log " : No Shutdown Requested, ($closexstore) received."
                }
}

# Restarts xStore
function Restart-Stack {
    param(
        [String]$restartStack
    )
    $warn.Visible = $true

            $warn.ForeColor = "black"
            $warn.BackColor = 'yellow'
            $warn.Text = "Attempting xStore Restart"

    #Add record to the list view field for action log.
    $time = (get-date -Format "HH:mm:ss")
    $listView.items.Insert(0,"[$time] Attempting Restart of xStore.")

        if($restartStack -ilike "y*" -or $CMDRestartStack -ilike "y*" -or $TRUE -eq $objTypeCheckboxRebootStack.Checked){

            Write-Log " : Attempting to start xStore via the start_eng.vbs file."
            Start-Process "Cscript.exe" -ArgumentList " C:\environment\start_eng.vbs //nologo" -WorkingDirectory 'C:\Windows\System32'

            $warn.ForeColor = "black"
            $warn.BackColor = 'yellow'
            $warn.Text = "Sleeping for 5 Seconds."
            Start-Sleep -Seconds 5

            $xstoreProcessList = @((Get-Process | Where-Object {$_.ProcessName -ilike "xstore*"}),(Get-Process | Where-Object {$_.ProcessName -ilike "xmob*"}),(Get-Process | Where-Object {$_.ProcessName -ilike "xenv*"}))
            $runningAfterRestart = ($xstoreProcessList.ProcessName).count

            if($runningAfterRestart -gt '0'){
                #Add record to the list view field for action log.
                $time = (get-date -Format "HH:mm:ss")
                $listView.items.Insert(0,"[$time] xStore has been launched.")

                $warn.BackColor = 'green'
                $warn.ForeColor = 'white'
                $warn.Text = "xStore Launched"

                #Add record to the list view field for action log.
                $time = (get-date -Format "HH:mm:ss")
                $listView.items.Insert(0,"[$time] INFO - xStore can take a while to load.")
            }else{
                $warn.BackColor = 'Red'
                $warn.ForeColor = 'white'
                $warn.Text = "ERROR - xStore Launch Failed."

                    #Add record to the list view field for action log.
                    $time = (get-date -Format "HH:mm:ss")
                    $listView.items.Insert(0,"[$time] ERROR - $runningAfterRestart number of xstore services launched. ")
            }

        }else{
            Write-log " : INFO - xstore restart not requested on $ENV:ComputerName. ($restartStack)."
        }
}

# Restarts The System
function invoke-SystemReboot {
    param(
        [String]$rebootMachine
    )

    $warn.Visible = $true

            $warn.ForeColor = "black"
            $warn.BackColor = 'yellow'
            $warn.Text = "Attempting System Reboot"

    #Add record to the list view field for action log.
    $time = (get-date -Format "HH:mm:ss")
    $listView.items.Insert(0,"[$time] Attempting Restart of $env:computername.")

        if($rebootmachine -ilike "y*" -or $TRUE -eq $objTypeCheckboxRebootMachine.Checked){

            Write-Log " : Rebooting $env:ComputerName."
            $listView.items.Insert(0,"[$time] System Reboot now.")
            Restart-Computer -Force | Out-Null

            $warn.ForeColor = "black"
            $warn.BackColor = 'yellow'
            $warn.Text = "Sleeping for 5 Seconds."
            Start-Sleep -Seconds 5

        }
}

Function Invoke-UIProcess {
    
    #$warn.Visible = $true

        #Add record to the list view field for action log.
        $time = (get-date -Format "HH:mm:ss")
        $listView.items.Insert(0,"[$time] Closing xStore Now")
            
            $warn.ForeColor = "black"
            $warn.BackColor = 'yellow'
            $warn.Text = "Attempting xstore close"

                close-xstore -closexstore 'y'

        #Add record to the list view field for action log.
        $time = (get-date -Format "HH:mm:ss")
        $listView.items.Insert(0,"[$time] xStore appears to be closed.")

            $warn.BackColor = 'green'
            $warn.ForeColor = 'white'
            $warn.Text = "xStore is closed."

            Start-Sleep -Seconds 1;

    if($TRUE -eq $objTypeCheckboxRebootMachine.Checked){
        
                $warn.ForeColor = "black"
                $warn.BackColor = 'yellow'   
                $global:warn.Text = "Invoking System Reboot"
                
                    invoke-SystemReboot -rebootMachine 'y'

    }elseIf($TRUE -eq $objTypeCheckboxRebootStack.Checked){
        
        $warn.ForeColor = "black"
        $warn.BackColor = 'yellow'   
        $global:warn.Text = "Attempting xStore Restart"
        
            Restart-Stack -restartStack 'y'
    }

    #Add record to the list view field for action log.
    $time = (get-date -Format "HH:mm:ss")
    $listView.items.Insert(0,"[$time] All Jobs Completed.")
        
        $warn.BackColor = 'green'
        $warn.ForeColor = 'white'
        $warn.Text = "Job Complete."
}

# This is for dealing with paramiterisation when calling from the command line. 
