    close-xstore -closexStore 'y'
    exit 0
}
If($CMDRestartSystem -ilike "y*"){
    close-xstore -closexStore 'y';
    invoke-SystemReboot -rebootMachine 'y';
    exit 0
}elseif($CMDRestartStack -ilike "y*"){
    close-xstore -closexStore 'y';
    Restart-Stack -restartStack 'y';
    exit 0
}

# If not called from the command line, show UI.
